package chapter01;

public class Ex08Aritymaticoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		final int TIME = 20000;
		int hour = TIME / 60 / 60;
		int minute = TIME / 60 % 60;
		int second = TIME % 60;
		System.out.println(TIME+"초는"+0+"시간"+0+"분"+0+"초입니다");
	}

}
